#include &lt;apr_uri.h&gt; #include &lt;apr_fnmatch.h&gt; #include &lt;unistd.h&gt; #include &quot;commands.h&quot; #include &quot;dbg.h&quot; #include &quot;bstrlib.h&quot; #include &quot;db.h&quot; #include &quot;shell.h&quot; int Command_depends(apr_pool_t * p, const char *path) { FILE *in = NULL; bstring line = NULL; in = fopen(path, &quot;r&quot;); check(in != NULL, &quot;Failed to open downloaded depends: %s&quot;, path); for (line = bgets((bNgetc) fgetc, in, '\n'); line != NULL; line = bgets((bNgetc) fgetc, in, '\n')) { btrimws(line); log_info(&quot;Processing depends: %s&quot;, bdata(line)); int rc = Command_install(p, bdata(line), NULL, NULL, NULL); check(rc == 0, &quot;Failed to install: %s&quot;, bdata(line)); bdestroy(line); } fclose(in); return 0; error: if (line) bdestroy(line); if (in) fclose(in); return -1; } int Command_fetch(apr_pool_t * p, const char *url, int fetch_only) { apr_uri_t info = {.port = 0 }; int rc = 0; const char *depends_file = NULL; apr_status_t rv = apr_uri_parse(p, url, &amp;info); check(rv == APR_SUCCESS, &quot;Failed to parse URL: %s&quot;, url); if (apr_fnmatch(GIT_PAT, info.path, 0) == APR_SUCCESS) { rc = Shell_exec(GIT_SH, &quot;URL&quot;, url, NULL); check(rc == 0, &quot;git failed.&quot;); } else if (apr_fnmatch(DEPEND_PAT, info.path, 0) == APR_SUCCESS) { check(!fetch_only, &quot;No point in fetching a DEPENDS file.&quot;); if (info.scheme) { depends_file = DEPENDS_PATH; rc = Shell_exec(CURL_SH, &quot;URL&quot;, url, &quot;TARGET&quot;, depends_file, NULL); check(rc == 0, &quot;Curl failed.&quot;); } else { depends_file = info.path; } // recursively process the devpkg list log_info(&quot;Building according to DEPENDS: %s&quot;, url); rv = Command_depends(p, depends_file); check(rv == 0, &quot;Failed to process the DEPENDS: %s&quot;, url); // this indicates that nothing needs to be done return 0; } else if (apr_fnmatch(TAR_GZ_PAT, info.path, 0) == APR_SUCCESS) { if (info.scheme) { rc = Shell_exec(CURL_SH, &quot;URL&quot;, url, &quot;TARGET&quot;, TAR_GZ_SRC, NULL); check(rc == 0, &quot;Failed to curl source: %s&quot;, url); } rv = apr_dir_make_recursive(BUILD_DIR, APR_UREAD | APR_UWRITE | APR_UEXECUTE, p); check(rv == APR_SUCCESS, &quot;Failed to make directory %s&quot;, BUILD_DIR); rc = Shell_exec(TAR_SH, &quot;FILE&quot;, TAR_GZ_SRC, NULL); check(rc == 0, &quot;Failed to untar %s&quot;, TAR_GZ_SRC); } else if (apr_fnmatch(TAR_BZ2_PAT, info.path, 0) == APR_SUCCESS) { if (info.scheme) { rc = Shell_exec(CURL_SH, &quot;URL&quot;, url, &quot;TARGET&quot;, TAR_BZ2_SRC, NULL); check(rc == 0, &quot;Curl failed.&quot;); } apr_status_t rc = apr_dir_make_recursive(BUILD_DIR, APR_UREAD | APR_UWRITE | APR_UEXECUTE, p); check(rc == 0, &quot;Failed to make directory %s&quot;, BUILD_DIR); rc = Shell_exec(TAR_SH, &quot;FILE&quot;, TAR_BZ2_SRC, NULL); check(rc == 0, &quot;Failed to untar %s&quot;, TAR_BZ2_SRC); } else { sentinel(&quot;Don't now how to handle %s&quot;, url); } // indicates that an install needs to actually run return 1; error: return -1; } int Command_build(apr_pool_t * p, const char *url, const char *configure_opts, const char *make_opts, const char *install_opts) { int rc = 0; check(access(BUILD_DIR, X_OK | R_OK | W_OK) == 0, &quot;Build directory doesn't exist: %s&quot;, BUILD_DIR); // actually do an install if (access(CONFIG_SCRIPT, X_OK) == 0) { log_info(&quot;Has a configure script, running it.&quot;); rc = Shell_exec(CONFIGURE_SH, &quot;OPTS&quot;, configure_opts, NULL); check(rc == 0, &quot;Failed to configure.&quot;); } rc = Shell_exec(MAKE_SH, &quot;OPTS&quot;, make_opts, NULL); check(rc == 0, &quot;Failed to build.&quot;); rc = Shell_exec(INSTALL_SH, &quot;TARGET&quot;, install_opts ? install_opts : &quot;install&quot;, NULL); check(rc == 0, &quot;Failed to install.&quot;); rc = Shell_exec(CLEANUP_SH, NULL); check(rc == 0, &quot;Failed to cleanup after build.&quot;); rc = DB_update(url); check(rc == 0, &quot;Failed to add this package to the database.&quot;); return 0; error: return -1; } int Command_install(apr_pool_t * p, const char *url, const char *configure_opts, const char *make_opts, const char *install_opts) { int rc = 0; check(Shell_exec(CLEANUP_SH, NULL) == 0, &quot;Failed to cleanup before building.&quot;); rc = DB_find(url); check(rc != -1, &quot;Error checking the install database.&quot;); if (rc == 1) { log_info(&quot;Package %s already installed.&quot;, url); return 0; } rc = Command_fetch(p, url, 0); if (rc == 1) { rc = Command_build(p, url, configure_opts, make_opts, install_opts); check(rc == 0, &quot;Failed to build: %s&quot;, url); } else if (rc == 0) { // no install needed log_info(&quot;Depends successfully installed: %s&quot;, url); } else { // had an error sentinel(&quot;Install failed: %s&quot;, url); } Shell_exec(CLEANUP_SH, NULL); return 0; error: Shell_exec(CLEANUP_SH, NULL); return -1; }  .\ex41\devpkg\commands.h
#ifndef _commands_h #define _commands_h #include &lt;apr_pools.h&gt; #define DEPENDS_PATH &quot;/tmp/DEPENDS&quot; #define TAR_GZ_SRC &quot;/tmp/pkg-src.tar.gz&quot; #define TAR_BZ2_SRC &quot;/tmp/pkg-src.tar.bz2&quot; #define BUILD_DIR &quot;/tmp/pkg-build&quot; #define GIT_PAT &quot;*.git&quot; #define DEPEND_PAT &quot;*DEPENDS&quot; #define TAR_GZ_PAT &quot;*.tar.gz&quot; #define TAR_BZ2_PAT &quot;*.tar.bz2&quot; #define CONFIG_SCRIPT &quot;/tmp/pkg-build/configure&quot; enum CommandType { COMMAND_NONE, COMMAND_INSTALL, COMMAND_LIST, COMMAND_FETCH, COMMAND_INIT, COMMAND_BUILD }; int Command_fetch(apr_pool_t * p, const char *url, int fetch_only); int Command_install(apr_pool_t * p, const char *url, const char *configure_opts, const char *make_opts, const char *install_opts); int Command_depends(apr_pool_t * p, const char *path); int Command_build(apr_pool_t * p, const char *url, const char *configure_opts, const char *make_opts, const char *install_opts); #endif  .\ex41\devpkg\db.c
#include &lt;unistd.h&gt; #include &lt;apr_errno.h&gt; #include &lt;apr_file_io.h&gt; #include &quot;db.h&quot; #include &quot;bstrlib.h&quot; #include &quot;dbg.h&quot; static FILE *DB_open(const char *path, const char *mode) { return fopen(path, mode); } static void DB_close(FILE * db) { fclose(db); } static bstring DB_load() { FILE *db = NULL; bstring data = NULL; db = DB_open(DB_FILE, &quot;r&quot;); check(db, &quot;Failed to open database: %s&quot;, DB_FILE); data = bread((bNread) fread, db); check(data, &quot;Failed to read from db file: %s&quot;, DB_FILE); DB_close(db); return data; error: if (db) DB_close(db); if (data) bdestroy(data); return NULL; } int DB_update(const char *url) { if (DB_find(url)) { log_info(&quot;Already recorded as installed: %s&quot;, url); } FILE *db = DB_open(DB_FILE, &quot;a+&quot;); check(db, &quot;Failed to open DB file: %s&quot;, DB_FILE); bstring line = bfromcstr(url); bconchar(line, '\n'); int rc = fwrite(line-&gt;data, blength(line), 1, db); check(rc == 1, &quot;Failed to append to the db.&quot;); return 0; error: if (db) DB_close(db); return -1; } int DB_find(const char *url) { bstring data = NULL; bstring line = bfromcstr(url); int res = -1; data = DB_load(); check(data, &quot;Failed to load: %s&quot;, DB_FILE); if (binstr(data, 0, line) == BSTR_ERR) { res = 0; } else { res = 1; } error: // fallthrough if (data) bdestroy(data); if (line) bdestroy(line); return res; } int DB_init() { apr_pool_t *p = NULL; apr_pool_initialize(); apr_pool_create(&amp;p, NULL); if (access(DB_DIR, W_OK | X_OK) == -1) { apr_status_t rc = apr_dir_make_recursive(DB_DIR, APR_UREAD | APR_UWRITE | APR_UEXECUTE | APR_GREAD | APR_GWRITE | APR_GEXECUTE, p); check(rc == APR_SUCCESS, &quot;Failed to make database dir: %s&quot;, DB_DIR); } if (access(DB_FILE, W_OK) == -1) { FILE *db = DB_open(DB_FILE, &quot;w&quot;); check(db, &quot;Cannot open database: %s&quot;, DB_FILE); DB_close(db); } apr_pool_destroy(p); return 0; error: apr_pool_destroy(p); return -1; } int DB_list() { bstring data = DB_load(); check(data, &quot;Failed to read load: %s&quot;, DB_FILE); printf(&quot;%s&quot;, bdata(data)); bdestroy(data); return 0; error: return -1; }  .\ex41\devpkg\db.h
#ifndef _db_h #define _db_h #define DB_FILE &quot;/usr/local/.devpkg/db&quot; #define DB_DIR &quot;/usr/local/.devpkg&quot; int DB_init(); int DB_list(); int DB_update(const char *url); int DB_find(const char *url); #endif  .\ex41\devpkg\dbg.h
