#include &lt;math.h&gt; #include &lt;lcthw/stats.h&gt; #include &lt;stdlib.h&gt; #include &lt;lcthw/dbg.h&gt; Stats *Stats_recreate(double sum, double sumsq, unsigned long n, double min, double max) { Stats *st = malloc(sizeof(Stats)); check_mem(st); st-&gt;sum = sum; st-&gt;sumsq = sumsq; st-&gt;n = n; st-&gt;min = min; st-&gt;max = max; return st; error: return NULL; } Stats *Stats_create() { return Stats_recreate(0.0, 0.0, 0L, 0.0, 0.0); } double Stats_mean(Stats * st) { return st-&gt;sum / st-&gt;n; } double Stats_stddev(Stats * st) { return sqrt((st-&gt;sumsq - (st-&gt;sum * st-&gt;sum / st-&gt;n)) / (st-&gt;n - 1)); } void Stats_sample(Stats * st, double s) { st-&gt;sum += s; st-&gt;sumsq += s * s; if (st-&gt;n == 0) { st-&gt;min = s; st-&gt;max = s; } else { if (st-&gt;min &gt; s) st-&gt;min = s; if (st-&gt;max &lt; s) st-&gt;max = s; } st-&gt;n += 1; } void Stats_dump(Stats * st) { fprintf(stderr, &quot;sum: %f, sumsq: %f, n: %ld, &quot; &quot;min: %f, max: %f, mean: %f, stddev: %f&quot;, st-&gt;sum, st-&gt;sumsq, st-&gt;n, st-&gt;min, st-&gt;max, Stats_mean(st), Stats_stddev(st)); }  .\ex43\stats_tests.c
