#undef NDEBUG #include &lt;stdlib.h&gt; #include &lt;sys/select.h&gt; #include &lt;stdio.h&gt; #include &lt;lcthw/ringbuffer.h&gt; #include &lt;lcthw/dbg.h&gt; #include &lt;sys/socket.h&gt; #include &lt;sys/types.h&gt; #include &lt;sys/uio.h&gt; #include &lt;arpa/inet.h&gt; #include &lt;netdb.h&gt; #include &lt;unistd.h&gt; #include &lt;fcntl.h&gt; struct tagbstring NL = bsStatic(&quot;\n&quot;); struct tagbstring CRLF = bsStatic(&quot;\r\n&quot;); int nonblock(int fd) { int flags = fcntl(fd, F_GETFL, 0); check(flags &gt;= 0, &quot;Invalid flags on nonblock.&quot;); int rc = fcntl(fd, F_SETFL, flags | O_NONBLOCK); check(rc == 0, &quot;Can't set nonblocking.&quot;); return 0; error: return -1; } int client_connect(char *host, char *port) { int rc = 0; struct addrinfo *addr = NULL; rc = getaddrinfo(host, port, NULL, &amp;addr); check(rc == 0, &quot;Failed to lookup %s:%s&quot;, host, port); int sock = socket(AF_INET, SOCK_STREAM, 0); check(sock &gt;= 0, &quot;Cannot create a socket.&quot;); rc = connect(sock, addr-&gt;ai_addr, addr-&gt;ai_addrlen); check(rc == 0, &quot;Connect failed.&quot;); rc = nonblock(sock); check(rc == 0, &quot;Can't set nonblocking.&quot;); freeaddrinfo(addr); return sock; error: freeaddrinfo(addr); return -1; } int read_some(RingBuffer * buffer, int fd, int is_socket) { int rc = 0; if (RingBuffer_available_data(buffer) == 0) { buffer-&gt;start = buffer-&gt;end = 0; } if (is_socket) { rc = recv(fd, RingBuffer_starts_at(buffer), RingBuffer_available_space(buffer), 0); } else { rc = read(fd, RingBuffer_starts_at(buffer), RingBuffer_available_space(buffer)); } check(rc &gt;= 0, &quot;Failed to read from fd: %d&quot;, fd); RingBuffer_commit_write(buffer, rc); return rc; error: return -1; } int write_some(RingBuffer * buffer, int fd, int is_socket) { int rc = 0; bstring data = RingBuffer_get_all(buffer); check(data != NULL, &quot;Failed to get from the buffer.&quot;); check(bfindreplace(data, &amp;NL, &amp;CRLF, 0) == BSTR_OK, &quot;Failed to replace NL.&quot;); if (is_socket) { rc = send(fd, bdata(data), blength(data), 0); } else { rc = write(fd, bdata(data), blength(data)); } check(rc == blength(data), &quot;Failed to write everything to fd: %d.&quot;, fd); bdestroy(data); return rc; error: return -1; } int main(int argc, char *argv[]) { fd_set allreads; fd_set readmask; int socket = 0; int rc = 0; RingBuffer *in_rb = RingBuffer_create(1024 * 10); RingBuffer *sock_rb = RingBuffer_create(1024 * 10); check(argc == 3, &quot;USAGE: netclient host port&quot;); socket = client_connect(argv[1], argv[2]); check(socket &gt;= 0, &quot;connect to %s:%s failed.&quot;, argv[1], argv[2]); FD_ZERO(&amp;allreads); FD_SET(socket, &amp;allreads); FD_SET(0, &amp;allreads); while (1) { readmask = allreads; rc = select(socket + 1, &amp;readmask, NULL, NULL, NULL); check(rc &gt;= 0, &quot;select failed.&quot;); if (FD_ISSET(0, &amp;readmask)) { rc = read_some(in_rb, 0, 0); check_debug(rc != -1, &quot;Failed to read from stdin.&quot;); } if (FD_ISSET(socket, &amp;readmask)) { rc = read_some(sock_rb, socket, 0); check_debug(rc != -1, &quot;Failed to read from socket.&quot;); } while (!RingBuffer_empty(sock_rb)) { rc = write_some(sock_rb, 1, 0); check_debug(rc != -1, &quot;Failed to write to stdout.&quot;); } while (!RingBuffer_empty(in_rb)) { rc = write_some(in_rb, socket, 1); check_debug(rc != -1, &quot;Failed to write to socket.&quot;); } } return 0; error: return -1; }  It&rsquo;s basically a DArray with dynamic start and end settings. You can also use a Queue of bstrings to do almost the same thing.
