#include &lt;stdlib.h&gt; #include &lt;stdio.h&gt; #include &lt;assert.h&gt; #include &lt;lcthw/dbg.h&gt; #include &lt;lcthw/tstree.h&gt; static inline TSTree *TSTree_insert_base(TSTree * root, TSTree * node, const char *key, size_t len, void *value) { if (node == NULL) { node = (TSTree *) calloc(1, sizeof(TSTree)); if (root == NULL) { root = node; } node-&gt;splitchar = *key; } if (*key &lt; node-&gt;splitchar) { node-&gt;low = TSTree_insert_base( root, node-&gt;low, key, len, value); } else if (*key == node-&gt;splitchar) { if (len &gt; 1) { node-&gt;equal = TSTree_insert_base( root, node-&gt;equal, key + 1, len - 1, value); } else { assert(node-&gt;value == NULL &amp;&amp; &quot;Duplicate insert into tst.&quot;); node-&gt;value = value; } } else { node-&gt;high = TSTree_insert_base( root, node-&gt;high, key, len, value); } return node; } TSTree *TSTree_insert(TSTree * node, const char *key, size_t len, void *value) { return TSTree_insert_base(node, node, key, len, value); } void *TSTree_search(TSTree * root, const char *key, size_t len) { TSTree *node = root; size_t i = 0; while (i &lt; len &amp;&amp; node) { if (key[i] &lt; node-&gt;splitchar) { node = node-&gt;low; } else if (key[i] == node-&gt;splitchar) { i++; if (i &lt; len) node = node-&gt;equal; } else { node = node-&gt;high; } } if (node) { return node-&gt;value; } else { return NULL; } } void *TSTree_search_prefix(TSTree * root, const char *key, size_t len) { if (len == 0) return NULL; TSTree *node = root; TSTree *last = NULL; size_t i = 0; while (i &lt; len &amp;&amp; node) { if (key[i] &lt; node-&gt;splitchar) { node = node-&gt;low; } else if (key[i] == node-&gt;splitchar) { i++; if (i &lt; len) { if (node-&gt;value) last = node; node = node-&gt;equal; } } else { node = node-&gt;high; } } node = node ? node : last; // traverse until we find the first value in the equal chain // this is then the first node with this prefix while (node &amp;&amp; !node-&gt;value) { node = node-&gt;equal; } return node ? node-&gt;value : NULL; } void TSTree_traverse(TSTree * node, TSTree_traverse_cb cb, void *data) { if (!node) return; if (node-&gt;low) TSTree_traverse(node-&gt;low, cb, data); if (node-&gt;equal) { TSTree_traverse(node-&gt;equal, cb, data); } if (node-&gt;high) TSTree_traverse(node-&gt;high, cb, data); if (node-&gt;value) cb(node-&gt;value, data); } void TSTree_destroy(TSTree * node) { if (node == NULL) return; if (node-&gt;low) TSTree_destroy(node-&gt;low); if (node-&gt;equal) { TSTree_destroy(node-&gt;equal); } if (node-&gt;high) TSTree_destroy(node-&gt;high); free(node); }  .\ex46\tstree_tests.c
